let UnhealthyCriteria = 30m;
let heartbeatThreshold = 5m;
let unhealthyThreshold = 30m;
let logs = 
  CommonSecurityLog
  | where TimeGenerated > startofday(ago(timeRangeQuery))
  | summarize LastHeartbeat = max(TimeGenerated) by Computer, DeviceVendor
  | extend State = iff(LastHeartbeat < ago(UnhealthyCriteria), 'Unhealthy', 'Healthy')
  | extend TimeFromNow = now() - LastHeartbeat
  | extend ["TimeAgo"] = strcat(toint(TimeFromNow / 1s), ' seconds')
  | project Computer, DeviceVendor, State, TimeFromNow, Source = 'Log';

let heartbeats =
  Heartbeat
  | where TimeGenerated > startofday(ago(timeRangeQuery))
  | summarize LastHeartbeat = max(TimeGenerated) by Computer
  | extend State = iff(LastHeartbeat < ago(heartbeatThreshold), 'Unhealthy', 'Healthy')
  | extend TimeFromNow = now() - LastHeartbeat
  | extend ["TimeAgo"] = strcat(toint(TimeFromNow / 1s), ' seconds')
  | project Computer, DeviceVendor='', State, TimeFromNow, Source = 'Heartbeat';

union logs, heartbeats
| summarize count() by Computer, State, Source
